const borderWidth = 1; // You can adjust the thickness of the border here


export function isCenterOf5x5GridOf0s(x: number, y: number, gameGrid: TwoDimensionalGrid) {

    // Check if there's enough space around the cell for a 5x5 grid
    if (y - 2 < 0 || y + 2 >= gameGrid.length || x - 2 < 0 || x + 2 >= gameGrid[0].length) {
        return false; // The cell is too close to the edge of the grid
    }

    // Loop through the 5x5 grid centered on (x, y)
    for (let i = y - 2; i <= y + 2; i++) {
        for (let j = x - 2; j <= x + 2; j++) {
            // Check if each cell in the 5x5 grid is not 0
            if (gameGrid[i][j] !== 0) {
                return false; // Found a cell in the 5x5 grid that is not 0
            }
        }
    }

    console.log('isCenterOf5x5GridOf0s', x, y);

    // All cells in the 5x5 grid are 8
    return true;

}

export default function DrawGameGrid(ctx: CanvasRenderingContext2D, gameState: tGameState) {

    ctx.fillStyle = "rgb(0,0,0)"

    const cellSize = gameCellSize(gameState)

    // were already translated under the header
    ctx.fillRect(0, 0, gameState.gameGrid[0].length * cellSize, BodyHeight());

    for (let y = 0; y < gameState.gameGrid.length; y++) {

        for (let x = 0; x < gameState.gameGrid[y].length; x++) {

            const cellX = x * cellSize;

            const cellY = y * cellSize;

            // Paths are not drawn as they are the default background
            switch (gameState.gameGrid[y][x]) {
                case 0:
                    // Draw path
                    ctx.fillStyle = 'rgba(98,74,74,0.37)'; // Black
                    ctx.fillRect(cellX, cellY, cellSize, cellSize);
                    break;
                case 1:
                    // Draw wall
                    ctx.fillStyle = '#FF00FF'; // Purple
                    ctx.fillRect(cellX, cellY, cellSize, cellSize);
                    break;
                case 2:
                    // Draw wall
                    ctx.fillStyle = '#31b0c3'; // Purple
                    ctx.fillRect(cellX, cellY, cellSize, cellSize);
                    break;
                default:
                case 3:
                    // Draw orb
                    ctx.fillStyle = '#05add3'; // Black
                    ctx.fillRect(cellX, cellY, cellSize, cellSize);
                    ctx.fillStyle = '#27c02a';
                    ctx.beginPath();
                    ctx.arc((cellX) + (cellSize / 2), (cellY) + (cellSize / 2), cellSize / 3, 0, Math.PI * 2);
                    ctx.fill();
                    break;
            }

            // Draw the border around the cell
            ctx.strokeStyle = 'rgba(15,15,15,0.23)'; // Black color for the border

            ctx.lineWidth = borderWidth;

            ctx.strokeRect(cellX + borderWidth / 2, cellY + borderWidth / 2, cellSize - borderWidth, cellSize - borderWidth);

        }

    }

}